#!/usr/bin/env node
var fs = require('fs');
var j2pdf = require('../json2pdf');

fs.readFile('data1', function(err,data) {
	var data= JSON.parse(data);
	var sett= {layout: 'landscape',size:'A4'};
	var output = fs.createWriteStream('actual.pdf');
	j2pdf(sett,data,output,function(page,all) {
		console.log('%d/%d',page,all);
	});
	
});

fs.readFile('data2', function(err,data) {

	var data= JSON.parse(data);
	var sett= {layout: 'landscape',size:'A4'};
	var output = fs.createWriteStream('actual2.pdf');
	j2pdf(sett,data,output,function(page,all) {
		console.log('%d/%d',page,all);
	});
});



